import React, {useState} from 'react'

export default function Games({game}) {
  const[ quantity , setquantity ] =useState(1)
  const [platform, setplatform] =useState('Microsoft Windows')
  return (
    <div style={{margin : '100px'}} className="shadow-lg p-3 mb-5 bg-white rounded">
      <h1>{game.name}</h1>
      <img src={game.image} alt="" className="img-fluid" style={{height:'200px',width:'200px'}}/>

      <div className="flex-container">

        <div className="m-1 w-100">
            <p>Platforms</p>
              <select className="form-select" value={platform} onChange={(e)=>{setplatform(e.target.value)}}>
                {game.platforms.map(platform => {
                  return <option value={platform}>{platform}</option>
                })}
              </select>
                    
        </div>

        <div className="m-1 w-100">
          <p>Quantity</p>
          <select className="form-select" value={quantity} onChange={(e)=>{setquantity(e.target.value)}}>
            {[...Array(10).keys()].map((x, i)=>{

              return <option value={i+1}>{i+1}</option>
            })}
          </select>
        </div>

      </div>

      <div className="flex-container">
            <div className="m-1 w-100">
              <h1>Price: {game.prices[0][platform]*quantity}</h1>
            </div>
            <div className="m-1 w-100">
              <button className="btn">ADD TO CART</button>

            </div>

      </div>
     
    </div>
  )
}
