import React from "react";
import { useSelector, useDispatch } from "react-redux";


export default function Cartpage() {
  const cartstate = useSelector(state=>state.cartReducer)
  const cartItems = cartstate.cartItems;
  return (
    <div>
      <div className="row justify-content-center">
        <div className="col-md-6">
          <h3 style={{ fontSize: "40px" }}>My Cart</h3>

          {cartItems.map(item=>{
            return <div className="flex-container">
    
                <div className='text-start m-1 w-100' >
                  <h1>{item.name} [{item.platform}]</h1>
                  <h1>Price: {item.quantity} * {item.prices[0][item.platform]} = {item.price}</h1>
                  <h1 style={{display: 'inline'}}>Quantity:</h1>
                  <i className="fa fa-plus" aria-hidden="true"></i>
                  <b>{item.quantity}</b>
                  <i className="fa fa-minus" aria-hidden="true"></i>
                  <hr/>
                </div>
                <div className='m-1 w-100'>
                  <img src={item.image} style={{height:'80px', width:'80px'}}></img>
                </div>
                <div className='m-1 w-100'>
                <i className="fa fa-trash mt-2" aria-hidden="true"></i>
                </div>
                 
              </div>
            
          })}
          
        </div>
        <div className="col-md-4"></div>
      </div>
    </div>
  );
}
