import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getAllGames } from "../actions/gameActions";
import Error from "../components/Error";
import Filter from "../components/Filter";
import Loading from "../components/Loading";

export default function Gameslist() {
  const dispatch = useDispatch();
  const gamesstate = useSelector((state) => state.getAllGamesReducer);
  const { games, error, loading } = gamesstate;
  useEffect(() => {
    dispatch(getAllGames());
  }, []);
  return (
    <div>
      <h2>Games List</h2>
      {loading && <Loading />}
      {error && <Error error="Something went wrong" />}

      <table className="table table-hover">
        <thead className="thead table-light">
          <tr>
            <th>Name</th>
            <th>Prices</th>
            <th>Category</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
        {games && games.map((games) => {
            return <tr>
                <td>{games.name}</td>
                <td>
                    PC : {games.prices[0]['PC']}<br/>
                    PS4 : {games.prices[0]['PS4']}<br/>
                    PS5 : {games.prices[0]['PS5']}
                </td>
                <td>{games.category}</td>
            </tr>
        })}
        </tbody>
      </table>
    </div>
  );
}
