import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import {BrowserRouter,Route,Routes, Switch } from "react-router-dom";
import Addgame from "./Addgame";
import Gameslist from "./Gameslist";
import Orderslist from "./Orderslist";

import Userslist from "./Userslist";

export default function Adminpage() {
  const userstate = useSelector((state) => state.loginUserReducer);
  const { currentUser } = userstate;
  const dispatch = useDispatch();

  useEffect(() => {
    if (!currentUser.isAdmin) {
      window.location.href = "/";
    }
  }, []);
  return (
    <div>
      <div className="row justify-content-center">
        <div className="col-md-10">
          <h2 style={{ fontSize: "35px" }}>Admin Panel</h2>

          <ul className="adminfunctions">
            <li>
              <a href="/admin/userlist">User List</a>
            </li>
            <li>
              <a href="/admin/gameslist">Games List</a>
            </li>
            <li>
              <a href="/admin/addgame">Add New Game</a>
            </li>
            <li>
              <a href="/admin/orderslist">Orders List</a>
            </li>
          </ul>
            
          <Switch>
          
              <Route path="/admin/userslist" component={Userslist} exact/>
              <Route path="/admin/orderslist" component={Orderslist} exact/>
              <Route path="/admin/pizzaslist" component={Gameslist} exact/>
              <Route path="/admin/addpizza" component={Addgame} exact/>
        
          </Switch>
            
        </div>
      </div>
    </div>
  );
}
