import React from "react";
import StripeCheckout from "react-stripe-checkout";

export default function Checkout({ subtotal }) {
  function tokenHander(token) {
    console.log(token);
  }
  return (
    <div>
      <StripeCheckout
        amount={subtotal*100}
        shippingAddress
        token={tokenHander}
        key='pk_test_51Kq2Z8A9BaFGzPyR6KVZsehmF7Cymn6z0y9I9dhAlMqvTdlMoQnqtOlRUwWbZeaugUsULnB9zcsXsf5OfTbVExmJ00TW3Ra1l8'
        currency='GBP'
      >
        <button className="btn">Pay Now</button>
      </StripeCheckout>
    </div>
  );
}
