import React , {useSate, useEffect} from 'react'
import { useDispatch , useSelector } from 'react-redux'
import { getUserOrdersById } from '../actions/orderActions'
import Error from "../components/Error";
import Loading from "../components/Loading";
import Success from "../components/Success";

export default function Orderspage() {

    const dispatch = useDispatch()
    const ordersstate = useSelector(state=>state.getUserOrdersByIdReducer)
    const {orders , error , loading} = ordersstate

    useEffect(() => {

        dispatch(getUserOrdersById())
    }, [])

  return (
    <div>
      <h2 style={{fontSize: '35px'}}>My Orders</h2>
      <div className="row justify-content-center" style={{backgroundColor: 'blue'}}>
          {loading && (<Loading/>)}
          {error && (<Error error='Something went wrong'>{error}</Error>)}
          {orders && orders.map(order=>{
              return <div className="col-md-8">

                  <div className="flex-container">
                        <div className='text-start w-100 m-1'>
                            <h2 style={{fontSize:'25px'}}>Items</h2>
                            {order.orderItems.map(item=>{
                                return <div>
                                    <h1>{item.name} [{item.platform}] * {item.quantity} ={item.price}</h1>
                                </div>
                            })}
                        </div>
                        <div className='text-start w-100 m-1'>
                            <h2 style={{fontSize:'25px'}}>Address</h2>
                            <h1>Street : {order.shippingAddress.street}</h1>
                            <h1>City : {order.shippingAddress.city}</h1>
                            <h1>Country : {order.shippingAddress.country}</h1>
                            <h1>Postcode : {order.shippingAddress.postcode}</h1>
                        </div>
                        <div className='text-start w-100 m-1'>
                            <h2 style={{fontSize:'25px'}}>Order info</h2>
                            <h1>Order Amount : {order.orderAmount}</h1>
                            <h1>Date : {order.createdAt.substring(0,10)}</h1>
                            <h1>Transaction Id : {order.transactionId}</h1>
                            <h1>Order Id : {order._id}</h1>   
                        </div>
                  </div>
              </div>
          })}
      </div>
    </div>
  )
}
