import React, {useState} from 'react'

export default function Games({game}) {
  const[ quantity , setquantity ] =useState(1)
  const [platform, setplatform] =useState('Microsoft Windows')
  return (
    <div>
      <h1>{game.name}</h1>
      <img src={game.image} className="img-fluid" style={{height:'200px',width:'200px'}}/>

      <div className="flex-container">

        <div className="w-100">
            <p>Platforms</p>
              <select>
                {game.platforms.map(platform => {
                  return <option value={platform}>{platform}</option>
                })}
              </select>
                    
        </div>

        <div className="w-100">
          <p>Quantity</p>
          <select>
            {[...Array(10).keys()].map((x, i)=>{

              return <option value={i+1}>{i+1}</option>
            })}
          </select>
        </div>

      </div>

      <div className="flex-container">
            <div>

            </div>
            <div>

            </div>

      </div>
     
    </div>
  )
}
