import React, { useState, useEffect } from "react";

export default function Addgame() {
  const [name, setname] = useState("");
  const [pcprice, setpcprice] = useState();
  const [ps4price, setps4price] = useState();
  const [ps5price, setps5price] = useState();
  const [image, setimage] = useState("");
  const [description, setdescription] = useState("");
  const [category, setcategory] = useState("");
  return (
    <div>
      <div>
        <h1>Add Game</h1>

        <form>
          <input
            className="form-control"
            type="text"
            placeholder="name"
            value={name}
            onChange={(e) => {
              setname(e.target.value);
            }}
          />
          <input
            className="form-control"
            type="text"
            placeholder="pc platform price"
            value={pcprice}
            onChange={(e) => {
              setpcprice(e.target.value);
            }}
          />
          <input
            className="form-control"
            type="text"
            placeholder="ps4 platform price"
            value={ps4price}
            onChange={(e) => {
              setps4price(e.target.value);
            }}
          />
          <input
            className="form-control"
            type="text"
            placeholder="ps5 platform price"
            value={ps5price}
            onChange={(e) => {
              setps5price(e.target.value);
            }}
          />
           <input
            className="form-control"
            type="text"
            placeholder="category"
            value={category}
            onChange={(e) => {
              setcategory(e.target.value);
            }}
          />
            <input
            className="form-control"
            type="text"
            placeholder="image url"
            value={image}
            onChange={(e) => {
              setimage(e.target.value);
            }}
          />
            <input
            className="form-control"
            type="text"
            placeholder="description"
            value={description}
            onChange={(e) => {
              setdescription(e.target.value);
            }}
          />
          
          <button className="btn" type='submit'>Add Game</button>
        </form>
      </div>
    </div>
  );
}
