export const addToCart=(game, quantity, platform)=>{




    var cartItem = {

        name : game.name , 
        _id : game._id ,
        image : game.image ,
        platform : game.platform ,
        quantity : quantity ,
        prices : game.prices ,
        price : game.prices[0][platform]* quantity
    }

    dispatch({type: 'ADD_TO_CART', payload : cartItem});
}
