export const getAllGamesReducer=(state={}, action)=>{

    switch(action.type){

        case 'GET_GAMES_REQUEST' : return{
            ...state
        }
        case 'GET_GAMES_SUCCESS' : return{
            games : action.payload
        }
        case 'GET_GAMES_FAILED' : return{
            error : action.payload
        }
        default : return state
    }

}
